import React, { useState } from 'react';
import './index.css';
import SearchBar from './components/SearchBar';
import PredictionChart from './components/PredictionChart';
import logo from './logo.png';

function App() {
  const [ticker, setTicker] = useState('');
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setTicker(e.target.value.toUpperCase());
    setError('');
  };

  const handleSearch = () => {
    if (!ticker) {
      alert("Please enter a ticker symbol.");
      return;
    }

    fetch(`http://127.0.0.1:5000/predict?ticker=${ticker}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`Backend returned ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        if (Array.isArray(data)) {
          setForecast(data);
        } else {
          setForecast(null);
          setError("Unexpected response format.");
        }
      })
      .catch((err) => {
        console.error(err);
        setForecast(null);
        setError(`No data found for ticker "${ticker}". Make sure it's supported.`);
      });
  };

  return (
    <div className="container">
      <img src={logo} alt="Stock Vision Logo" className="logo" />
      <h1>Stock Vision</h1>
      <p>AI-Powered Stock Forecasting</p>

      <SearchBar ticker={ticker} onChange={handleChange} onSearch={handleSearch} />

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {forecast && forecast.length > 0 && (
        <>
          <PredictionChart data={forecast} />
          <p>
            Predicted price for {ticker} on {forecast[0].date}: ${forecast[0].yhat.toFixed(2)} (range: ${forecast[0].yhat_lower.toFixed(2)} - ${forecast[0].yhat_upper.toFixed(2)})
          </p>
        </>
      )}
    </div>
  );
}

export default App;
