import React, { useState } from 'react';
import './index.css';
import SearchBar from './components/SearchBar';
import PredictionChart from './components/PredictionChart';
import logo from './logo.png';

const VALID_TICKERS = ['COST', 'AMZN'];

function App() {
  const [ticker, setTicker] = useState('');
  const [forecast, setForecast] = useState(null);

  const handleChange = (e) => setTicker(e.target.value.toUpperCase());

  const handleSearch = () => {
    const upperTicker = ticker.toUpperCase();
    if (!VALID_TICKERS.includes(upperTicker)) {
      alert(`Only the following tickers are supported: ${VALID_TICKERS.join(', ')}`);
      return;
    }

    fetch(`http://127.0.0.1:5000/predict?ticker=${upperTicker}`)
      .then((res) => {
        if (!res.ok) throw new Error('Network response was not ok');
        return res.json();
      })
      .then((data) => {
        if (Array.isArray(data)) {
          setForecast(data);
        } else {
          alert('No forecast data found for this ticker.');
        }
      })
      .catch((err) => {
        console.error('Error fetching prediction:', err);
        alert('Error fetching data. Please check your backend or the browser console.');
      });
  };

  return (
    <div className="container">
      <img src={logo} alt="Stock Vision Logo" className="logo" />
      <h1>Stock Vision</h1>
      <p>AI-Powered Stock Forecasting</p>
      <SearchBar ticker={ticker} onChange={handleChange} onSearch={handleSearch} />
      {forecast && forecast.length > 0 && (
        <>
          <PredictionChart data={forecast} />
          <p>
            Predicted price for {ticker.toUpperCase()} on {forecast[0].date}: $
            {forecast[0].yhat.toFixed(2)} (range: $
            {forecast[0].yhat_lower.toFixed(2)} - ${forecast[0].yhat_upper.toFixed(2)})
          </p>
        </>
      )}
    </div>
  );
}

export default App;
